import React from 'react';
import AOS from 'aos';

import {
  BrowserRouter as Router,
  Link,
  Route,
  Redirect,
  Switch,
  useHistory
} from "react-router-dom"

import Landing from 'views/Lading';
import Login from './views/Login';
import Signup from './views/Signup';
import Daycard from "components/Daycard";
import Profile from 'views/Profile';
import Navbar from 'components/Navbar';
import ContentSection from 'components/ContentSection';
import Search from 'components/Search';

AOS.init();

const fakeAuth =  {
  isAuthenticated: false,
  authneticate(cb) {
    this.isAuthenticated = true
    setTimeout(cb, 100)
  },
  signout(cb) {
    this.isAuthenticated = false
    setTimeout(cb, 100)
  }
}

class App extends React.Component {
  constructor(){
    super(); 
    this.state = {
      isAuthenticated: false,
      user: null,
      redirectToReferrer: false
    }
  }


//signup method, sends data to backend to create a user
//authenticates and sets its state to true! 
  handleSubmit = (username, email, password) => {
    fetch('http://localhost:9000/signup', {
      method:'POST',
      headers: {
        'content-type': 'application/json'
      },
      body:JSON.stringify({
        username,email,password
      })
    })
    .then(res => res.json())
    .then(data => {
      if(data) {
        if(data.message) {
          console.log(data.message)
        }
        else if(data.user) {
          this.setState({user:data, redirect:'/', isAuthenticated: true})
        }
          
        console.log(data);
      }
    }
    )
    .catch(err => {
      console.log(err);
    })
  }

  login = () => {
    fakeAuth.authenticate(() => {
      this.setState(() => ({
        redirectToReferrer: true
      }))
    })
  }


  render() {
    const { isAuthenticated, redirect } = this.state; 
    console.log('authneticate:',isAuthenticated, redirect);

    return (
        <div>
          <Switch>
            <Route path='/' exact >
              <Landing />
              <ContentSection />
            </Route>
            <Route path='/login' render={ () => <Login />} />
            <Route path='/signup' render={ () => <Signup redirect={redirect} 
              handleSubmit={this.handleSubmit} />} />
            <Route path='/profile' render={ () => <Profile />} />
          </Switch>
        </div>
    )
  }


}
export default App;
