import React from 'react';
// import AOS from 'aos';

import {
  Link,
  Route,
  Redirect,
  Switch,
  useHistory
} from "react-router-dom"
import PrivateRoute from './components/PrivateRoute';
// import SignOut from './components/SignOut';

import Landing from 'views/Lading';
import Login from './views/Login';
import Signup from './views/Signup';
import Daycard from "components/Daycard";
import Profile from 'views/Profile';
import Navbar from 'components/Navbar';
import ContentSection from 'components/ContentSection';
import Search from 'components/Search';


// AOS.init();

class App extends React.Component {
  constructor(){
    super(); 
    this.state = {
      isAuthenticated: false,
      user: [],
      redirect: null
    }
  }


//signup method, sends data to backend to create a user
//authenticates and sets its state to true! 
  handleSubmit = (username, email, password) => {
    // const { username, email, password } = this.state
    console.log('handlesubmit:', username, email, password);
    fetch('http://localhost:9000/signup', {
      method:'POST',
      headers: {
        'content-type': 'application/json'
      },
      body:JSON.stringify({
        username,email,password
      })
    })
    .then(res => res.json())
    .then(data => {
      // console.log(data);
      if(data) {
        if(data.message) {
          console.log(data.message)
        }
        else if(data.user) {
          this.setState({user:data, redirect:'/', isAuthenticated: true})
        }
          
        console.log(data);
      }
    }
    )
    .catch(err => {
      console.log(err);
    })
  }

  //signin method to send the data to the server 
  ontextSubmit = (email, password) => {
    // const { email, password } = this.state
    console.log('ontextSubmit:', email, password);
    fetch('http://localhost:9000/login', {
      method: 'POST',
      headers: {
        'content-type': 'application/json'
      },
      body:JSON.stringify({
        email,password
      })
    })
    .then(res => res.json())
    .then(data => {
      // console.log(data);
      if(data) {
        if(data.message) {
          console.log(data.message)
        }
        else if(data.user) {
          
          this.setState({user:data, redirect:'/', isAuthenticated: true})
         
        }
          
        console.log(data);
      }
    })
    .catch(err => {
      console.log('error:', err);
    })
  }


//  privateRoute =({ component: Component, ...rest}) => {
//     return (
//       <Route {...rest} render={(props) => (
//         this.state.isAuthenticated === true
//           ? <Component {...props}/>
//           :<Redirect to='/login'/>
//       )}/>
//     )
//   }


  render() {
    const { redirect, isAuthenticated } = this.state; 
    console.log('authneticate:',isAuthenticated, redirect);
    console.log('user app', this.state.user);
    return (
        <div>
          <Navbar />
          {/* <Route path='/logout' render={ () => <SignOut redirect={redirect} />} /> */}
          <Switch>
          <Route isAuthenticated={isAuthenticated} path='/' exact >
              <Landing user={this.state.user} />
              <ContentSection />
            </Route>
            <Route path='/login' render={ () => <Login redirect={redirect} 
              ontextSubmit={this.ontextSubmit} />} />
            <Route path='/signup' render={ () => <Signup redirect={redirect} 
              handleSubmit={this.handleSubmit} />} />
           <PrivateRoute isAuthenticated={isAuthenticated} path="/profile">
            <Profile />
          </PrivateRoute>
          </Switch>
        </div>
    )
  }

}
export default App;
