import {TEXT} from './action'

let initText = {
    searchText: '', 
}

export const getText = (state=initText, action={}) =>{
    switch (action.type) {
        case TEXT:
            console.log('reducer:', action.type);
        return {...state, searchText:state.payload}
    default:
        return {...state}
    }
}
