import React from 'react';
// import search from './search.css'
// import { connect } from 'react-redux'
// import { getText, setText, setCity } from '../redux/action'
const APIKEY = 'MAjIj3A4LIsJqoNrDn74HNDnANduKaUw'


const onTextChange = (e) => {
    const value = e.target.value;
    // const {city} = this.state
    if(value.length > 0) {
        fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${APIKEY}&q=${value}`)
        .then(response => response.json())
        .then(data => {
            console.log(data);
            // this.setState({city:})
        })
        .catch(err => {
            console.log(err);
        })
    }
        // suggestions = city( val => {
        //     return val.LocalizedName.toLowerCase().startsWith(value.toLowerCase())
        // })
        // this.setState({text:value, suggestions})
    // else{
        // this.setState({text:value})
    // }
}

const onTextClicked = (key) => {
    console.log(key);
}

const renderSuggestion = () => {
    const {suggestions} =this.state;
    return (
        <ul>
        {
            suggestions.map(item => {
                return <li key={item.id}
                        onClick={()=> onTextClicked(item.key)}>
                            {item.LocalizedName}
                        </li>
            })
        }
        </ul>
    )
}


const Search = () => {
    // const { onTextChange, setText, searchText } = props
    // console.log('stext', searchText);
    return (
        <div className="form-inline active-cyan-3 active-cyan-4">
            <input type="text" 
            placeholder="Search"
            onChange={onTextChange}
            className="form-control form-control-sm ml-3 w-75"/>
        </div>
    )
}


export default Search;
