import React from 'react';
import Landing from 'views/Lading';
import AOS from 'aos';
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

import Login from './views/Login';
import Signup from './views/Signup';
import Daycard from "components/Daycard";
import Profile from 'views/Profile';
import Navbar from 'components/Navbar';
import ContentSection from 'components/ContentSection';
import Search from 'components/Search';
// import Authentify from 'components/Authentify';


AOS.init();

class App extends React.Component {
  constructor(){
    super(); 
    this.state = {
      isAuthenticated: false,
      user: null,
      redirect: null
    }
  }

  handleSubmit = () => {
    const {username, email, password} = this.state;
    fetch('http://localhost:9000/signup', {
      method:'POST',
      headers: {
        'content-type': 'application/json'
      },
      body:JSON.stringify({
        username,email,password
      })
    })
    .then(res => res.json())
    .then(data => {
      if(data) {
        this.setState({user:data, redirect:'/', isAuthenticated: true})
      }
    }
    )
    .catch(err => {
      console.log(err);
    })
  }


  render() {
    const { isAuthenticated, redirect } = this.state; 
    if(redirect) {
      return <Redirect to = {redirect} />
    }
    return (
        <div>
          <Navbar />
          <Switch>
            <Route path='/' exact >
              <Landing />
              <ContentSection />
            </Route>
            <Route path='/login' render={ () => <Login />} />
            <Route path='/signup' render={ () => <Signup ziv={this.handleSubmit} />} />
            <Route path='/profile' render={ () => <Profile />} />
          </Switch>
        </div>
    )
  }


}
export default App;
