export const TEXT = 'TEXT ';
export const SAMTEXT = 'SAMTEXT ';
export const CITY = 'CITY ';

const APIKEY = 'MAjIj3A4LIsJqoNrDn74HNDnANduKaUw'

export const getText = (text) => {
    console.log(text);
    return {
        type: TEXT,
        payload: text
    }
}

// city api: 
// dataservice.accuweather.com/locations/v1/search?apikey=MAjIj3A4LIsJqoNrDn74HNDnANduKaUw&q=Tel%20Aviv
//5 days api: 
// http://dataservice.accuweather.com/forecasts/v1/daily/5day/215854?apikey=MAjIj3A4LIsJqoNrDn74HNDnANduKaUw
// http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${APIKEY}&q=Tel"


export const setText = (text) => {
    // console.log('setText', text);
    return (dispatch) => {
        fetch(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${text}?apikey=${APIKEY}`)
        .then(response => response.json())
        .then(data => {
            console.log(data);
            dispatch({type:SAMTEXT,payload:data})
        })
        .catch(err => {
            console.log(err);
        })
    }
}

export const setCity = (city) => {
    // console.log('setText', text);
    return (dispatch) => {
        fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${APIKEY}&q=${city}`)
        .then(response => response.json())
        .then(data => {
            console.log(data);
            dispatch({type:SAMTEXT,payload:data})
        })
        .catch(err => {
            console.log(err);
        })
    }
}
