export default class Profile extends React.Component{
  constructor () {
    super();
    this.state = {
      citylist: [],
      suggestions: [],
      key: ''
  }
  }

//change this to fetch from favorites DB 
  componentDidMount(){
    // this.setState({suggestions:cities});
