import React from 'react';
// import search from './search.css'
import { connect } from 'react-redux'
import { getText, setText, setCity } from '../redux/action'


onTextChange = (e) => {
    const value = e.target.value;
    const {city} = this.state
    let suggestions =[];
    if(value.length > 0) {
        suggestions = city( val => {
            return val.LocalizedName.toLowerCase().startsWith(value.toLowerCase())
        })
        this.setState({text:value, suggestions})
    }
    else{
        this.setState({text:value})
    }
}

onTextClicked = (key) => {
    console.log(key);
}

const Search = (props) => {
    const { handleText, setText, searchText } = props
    // console.log('stext', searchText);
    return (
        <div className="form-inline active-cyan-3 active-cyan-4">
            <input type="text" 
            placeholder="Search"
            onChange={handleText}
            className="form-control form-control-sm ml-3 w-75"/>
            <button onClick={() => setText(searchText)} 
            className="btn-outline-success btn" type='search'>search</button> 
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        searchText: state.getText.searchText,
        res:state.setText.res
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleText: (e) => dispatch(setCity(e.target.value)),
        setText: (param1) => dispatch(setText(param1))
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Search);
