export const TEXT = 'TEXT ';
export const SAMTEXT = 'SAMTEXT ';


export const getText = (text) => {
    console.log(text);
    return {
        type: TEXT,
        payload: text
    }
}

export const setText = (text) => {
    return (dispatch) => {
        fetch(`https://jsonplaceholder.typicode.com/posts/?userId=${text}`)
        .then(response => response.json())
        .then(data => {
            console.log(data);
            dispatch({type:SAMTEXT,payload:data})
        })
        .catch(err => {
            console.log(err);
        })
    }
}
